package com.basic;

public class GreaterNumber2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int a=150,b=30,c=45;
        if(a>b && a>c) {
        	System.out.println("a is greater");
        }
        	else if(b>a && b>c) {
        		System.out.println("b is greater");
        	}
        		else
        		{
        			System.out.println("c is greater");
        		}
	}
        	
}  
		
		
		
	

