package com.demo;

public class ForLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int[] array= {10,25,30,45,50,65,70};
		
		for(int i=0;i<array.length;i++) {
			
			if(array[i]%2==0) {
				
				System.out.println(array[i]);
				
			}
			
			
		}
		
		
	}

}
