package com.demo;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		int b = 20;
		int c = 30;                                                               

		if (a > b || b == c)//false||true=true;
		{
		
		int d=a+b+c;
		System.out.println(d);

	}
		else {
			System.out.println(" condition is failed");
			
		}

}}
