package com.demo;

public class Ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int num=10;
      
      if(num%2==0) {
    	  System.out.println("The Given number is even="+num);
      }
      else {
    	  System.out.println("The given number is odd="+num);
      }
	}

}
