package com.demo;

public class SwitchExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int number=6;
		String color ="";
		switch(number) {
		case  1: 
			color="red";
			break;
		case 2:
			color="yellow";
			break;
		case 3:
			color="blue";
			break;
		case 4:
			color="green";
		break;
			default:
				color="not available";
		}
		System.out.println(color);
		
		
		
		
		
		
		
		}
		
		
		
		
		
		
		
	}


