package com.demo;

public class SwitchStatment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int age=25;
		
		switch (age) {
		
		case 20:
			System.out.println("your ding a dgree");
			break;
		case 25:
			System.out.println("you completed your education");
			break;
		case 30:
			System.out.println("your doing a job");
			break;
		case 40:
			System.out.println("your probably married");
			break;
			
			default:
				System.out.println("invalid age");
			
		}

	}

}
